float qdotproduct(vector4 q1, q2)
{
    return q1.x * q2.x + q1.y * q2.y + q1.z * q2.z + q1.w * q2.w;
}

vector4 qnormalize(vector4 q)
{
    int len = len(q);
    float sum=0;
    for(int i = 0; i < len; i++)
    {
        sum += pow(q[i],2);
    }
    sum = pow(sum,0.5);

    return q/sum;
}

vector4 q1 = p@orient;
q1 = qnormalize(q1);
vector4 q2 = p@opinput1_orient;
q2 = qnormalize(q2);
float dot = qdotproduct(q1, q2);

if(dot>0)
{
    q2 = -q2;
    dot = -dot;
}


float theta = acos(dot);
theta = theta * ch("bias");

vector4 q3 = q2 - q1 * dot;
q3 = qnormalize(q3);

p@orient = q1 * cos(theta) + q3 * sin(theta);

